package com.livedemo;

public class KiloToMeterConver {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
                    int kilometer=9 ,meter,centimeter;
                    meter = kilometer*1000;      //1kilometer = 1000meter
                    System.out.println("Conversion of "+kilometer+" kilometer to meter="+meter);
                    centimeter = kilometer*100000;   ////1kilometer = 100000centimeter
                    System.out.println("Conversion of "+kilometer+" kilometer to centimeter="+centimeter);
                	
	}

}
